import XCTest
@testable import iParapheur


class Crypto_ImprimerieNationale_InSignedData_Tests: XCTestCase {

    func testDecodeFull() {
        
        let jsonString = """
            {
                "result": {
                    "certificateId": "E828BD080FA00000050450524F2002030101",
                    "signedData": "",
                    "tokenData": {
                        "token": {
                            "label": "C Carte Pass-IN",
                            "manufacturerId": "Imprimerie Nationale",
                            "serialNumber": "6367610002000000898F"
                        },
                        "certificates": [{
                            "id": "E828BD080FA00000050450524F2002030101",
                            "value": ""
                        }, {
                            "id": "E828BD080FA00000050450524F2002030108",
                            "value": ""
                        }],
                        "middleware": {
                            "description": "MW Mobile",
                            "version": "0.6.1",
                            "manufacturerId": "Imprimerie Nationale"
                        }
                    }
                },
                "status": {
                    "description": "OK",
                    "code": "00"
                }
            }
        """
        let jsonData = jsonString.data(using:.utf8)!
        
        let jsonDecoder = JSONDecoder()
        let inSignedData = try! jsonDecoder.decode(InSignedData.self, from:jsonData)
        
        // Checks
        
        XCTAssertNotNil(inSignedData)
        let signedData = CryptoUtils.data(hex: inSignedData.signedData)
        let signedDataB64 = signedData.base64EncodedString()
        
        XCTAssertEqual(signedDataB64, "")
    }
    
}
